function rdLoading() {
    return {
        restrict: "AE",
        template: '<div class="loading"><div class="double-bounce1"></div><div class="double-bounce2"></div></div>'
    };
}

function rdWidgetBody() {
    return {
        requires: "^rdWidget",
        scope: {
            loading: "@?",
            classes: "@?"
        },
        transclude: !0,
        template: '<div class="widget-body" ng-class="classes"><rd-loading ng-show="loading"></rd-loading><div ng-hide="loading" class="widget-content" ng-transclude></div></div>',
        restrict: "E"
    };
}

function rdWidgetFooter() {
    return {
        requires: "^rdWidget",
        transclude: !0,
        template: '<div class="widget-footer" ng-transclude></div>',
        restrict: "E"
    };
}

function rdWidgetTitle() {
    return {
        requires: "^rdWidget",
        scope: {
            title: "@",
            icon: "@"
        },
        transclude: !0,
        template: '<div class="widget-header"><i class="fa" ng-class="icon"></i> {{title}} <div class="pull-right" ng-transclude></div></div>',
        restrict: "E"
    };
}

function rdWidget() {
    return {
        transclude: !0,
        template: '<div class="widget" ng-transclude></div>',
        restrict: "EA"
    };
}

function AlertsCtrl(e) {
    e.alerts = [ {
        type: "success",
        msg: "Thanks for visiting! Feel free to create pull requests to improve the dashboard!"
    }, {
        type: "danger",
        msg: "Found a bug? Create an issue with as many details as you can."
    } ], e.addAlert = function() {
        e.alerts.push({
            msg: "Another alert!"
        });
    }, e.closeAlert = function(t) {
        e.alerts.splice(t, 1);
    };
}

function MasterCtrl(t, e) {
    t.getWidth = function() {
        return window.innerWidth;
    }, t.$watch(t.getWidth, function(g) {
        t.toggle = 992 <= g && (!angular.isDefined(e.get("toggle")) || !!e.get("toggle"));
    }), t.toggleSidebar = function() {
        t.toggle = !t.toggle, e.put("toggle", t.toggle);
    }, window.onresize = function() {
        t.$apply();
    };
}

angular.module("RDash", [ "ui.bootstrap", "ui.router", "ngCookies" ]), angular.module("RDash").config([ "$stateProvider", "$urlRouterProvider", function(t, e) {
    e.otherwise("/"), t.state("index", {
        url: "/",
        templateUrl: "templates/dashboard.html"
    }).state("tables", {
        url: "/tables",
        templateUrl: "templates/tables.html"
    });
} ]), angular.module("RDash").directive("rdLoading", rdLoading), angular.module("RDash").directive("rdWidgetBody", rdWidgetBody), 
angular.module("RDash").directive("rdWidgetFooter", rdWidgetFooter), angular.module("RDash").directive("rdWidgetHeader", rdWidgetTitle), 
angular.module("RDash").directive("rdWidget", rdWidget), angular.module("RDash").controller("AlertsCtrl", [ "$scope", AlertsCtrl ]), 
angular.module("RDash").controller("MasterCtrl", [ "$scope", "$cookieStore", MasterCtrl ]);